class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();

        vector<int> vec(n + k - 1);
        for(int i = 0; i < vec.size(); i++)
        {
            vec[i] = colors[i % n];
        }

        int ct = 0, res = 0;
        for(int i = 0; i < vec.size(); i++)
        {
            ct++;
            if(ct >= k)
            {
                res++;
            }
            if((i + 1) < vec.size() && vec[i] == vec[i + 1])
            {
                ct = 0;
            }
        }
        return res;
    }
};
