class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {

        int num=0;
        int i=0,j=1;
        int n=col.size();
        while(j<n+k && i<n){
            int temp =j;
            int te=temp%n;
            while(col[te]!=col[(te-1+n)%n]&&temp<(n+k-1)){
                temp++;
                te=temp%n;
            }
            num+=std::max(temp-i-k+1,0);
            i=temp;
            j=i+1;
}
        return num;
    }
};
