class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        vector<int> v=c;
        int n=v.size();
        for(auto &i:c)v.emplace_back(i);
        vector<int> till_idx(2*n,0);
        int i=0;
        while(i<2*n){
            int j=i+1;
            while(j<2*n && v[j-1]!=v[j])j++;
            int len=j-i;
            for(int l=i;l<j;l++)till_idx[l]=len--;
            i=j;
        }
        int res=0;
        for(int i=0;i<n;i++){
            if(till_idx[i]>=k)res++;
        }
        return res;


    }
};
