#include <vector>

class Solution {
public:
    int numberOfAlternatingGroups(std::vector<int>& C, int k) {
        int n = C.size();
        if (k > n) {
            return 0; // If k is greater than the size of C, no valid groups can exist
        }

        // Extend C to simulate circular array
        std::vector<int> extendedC(C.begin(), C.end());
        extendedC.insert(extendedC.end(), C.begin(), C.begin() + k - 1);

        int R = 0; // Count of alternating groups
        int currentLength = 1; // Length of the current alternating sequence

        // Traverse the extended array to count alternating groups
        for (int i = 1; i < n + k - 1; ++i) {
            if (extendedC[i] != extendedC[i - 1]) {
                ++currentLength;
            } else {
                currentLength = 1;
            }

            if (currentLength >= k) {
                ++R; // Increment the count of alternating groups
            }
        }

        return R; // Return the total count of alternating groups
    }
};



