class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        suma = len(colors)
        suha = colors + colors[:k-1]

        res = 0
        N = 1
        for i in range(1, len(suha)):
            if suha[i] != suha[i - 1]:
                N += 1
            else:
                N = 1
            if N >= k:
                res += 1

        return res
