class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        int currLength = 1;
        int start = 0;
        int end = 1;
        while(end < n+k-1){
            if(colors[end%n] != colors[(end-1)%n]){
                currLength++;
            }
            else{
                currLength = 1;
                start = end;
            }
            if(currLength == k){
                ans++;
                currLength--;
                start++;
            }
            end++;
        }
        return ans;
    }
};
