class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int pre=1;
        int cnt=0;
        int n=colors.length;
        for (int i = 1; i < colors.length+k-1; i++) {
            if(colors[(i+n-1)%n]!=colors[i%n]){
                pre++;
            }else {
                pre=1;
            }
            if(pre>=k){
                cnt++;
            }
        }
        return cnt;
    }
}
