class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int last=col[0];
        int c=1;
        int ans=0;
        int n=col.size();
        for(int i=1;i<n+k-1;i++){
            int ind=i%n;
            if(col[ind]!=last){
                c++;
            }
            else{
                if(c>=k){
                    ans+=c-k+1;
                }
                c=1;
            }
            last=col[ind];
        }
        if(c>=k){
            ans+=c-k+1;
        }
        return ans;
    }
};
