class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int length) {
        int size = nums.size();
        vector<int> alternatingCounts(size, 0);

        for (int i = 0; i < 2 * size - 1; i++) {
            if (nums[(i - 1 + size) % size] != nums[i % size]) {
                alternatingCounts[i % size] = max(alternatingCounts[i % size], 1 + (i ? alternatingCounts[(i - 1 + size) % size] : 0));
            } else {
                alternatingCounts[i % size] = max(alternatingCounts[i % size], 1);
            }
        }

        int total = 0;
        for (int i = 0; i < alternatingCounts.size(); i++) {
            if(alternatingCounts[i] >= length)
                total++;
        }
        return total;
    }
};
