class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();

        int i=1;
        int curr = 1;
        int ans = 0;

        for(int j=0; j<n+k-2; j++){
            int l = i-1;
            if(l==-1){
                l = n-1;
            }

            if(a[l]!=a[i]){
                curr++;
            }
            else{
                curr = 1;
            }

            if(curr >= k){
                ans++;
            }

            i++;
            if(i == n){
                i = 0;
            }
        }

        return ans;
    }
};
