class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors += colors[:k - 1]
        ret = 0
        cur = 1
        prv = colors[0]
        for c in colors[1:]:
            if prv != c:
                cur += 1
                if cur >= k:
                    ret += 1
            else:
                cur = 1
            prv = c
        return ret
