class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        for(int i=0; i<(k-1); i++)
        {
            a.push_back(a[i]);
        }
        n+=(k-1);
        int ans = 0;
        vector<int>pre(n,-1);
        for(int i=1; i<n; i++)
        {
            if(a[i]==a[i-1])
            {
                pre[i] = i;
            }else{
                pre[i] = pre[i-1];
            }
        }
        for(int i=0; i<=(n-k); i++)
        {
            if(pre[i]==pre[i+k-1])
            {
                ans++;
                //if(ans==2) return i;
            }
        }
        return ans;
    }
};
