class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int i = 0;
        int j = 1;
        int n = colors.size();

        while(j < n+k && i < n){
            int temp = j;
            int end = temp % n;

            while(colors[end] != colors[(end-1+n) % n] && temp < n-1+k){
                temp++;
                end = temp % n;
            }
            ans += max(temp -i-k+1 , 0);
            i = temp;
            j=i+1;
        }
        return ans;
    }
};
