class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = arr.size();
        int i=1;
        bool ok = true;
        int cnt=0;
        int len=1;
        vector<int> colors(n+k);
        for(int i=0;i<n+k;i++) {
            colors[i] = arr[i%n];
           // cout << colors[i] << ' '; 
        }
             //   cout << endl;

        while(i<n+k) {
            if(colors[i]!=colors[i-1]) {
                len++;
//                cout << len << ' ';
                if(len==k and i-k+1<n) {
                    cnt++;
                    len--;
                }
            } else {
                //cout << i << ' ';
                len=1;
            }
            i++;
        }
        cout << endl;
        return cnt;

    }
};
