class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int n=colors.size();
        int v=0;

        int len=1;
        int i=1;
        while(i<n+k-1)
        {
            int cur=i;
            int prev=i-1;
            if(prev >= n) 
            prev-=n;
            if(cur >= n) 
            cur-=n;
            if(colors[cur] == colors[prev]) 
            len=1;
            else 
            len++;
            if(len >= k) 
            v++;
            i++;
        }

        if(k==1) 
        return n;
        return v;
    }
};
