class Solution:
    def numberOfAlternatingGroups(self, A: List[int], k: int) -> int:
        n = len(A)
        A += A
        cur = 1
        res = 0
        for i in range(1, n):
            if A[i] == A[i - 1]:
                cur = 0
            cur += 1
        for i in range(n, n + n):
            if A[i] == A[i - 1]:
                cur = 0
            cur += 1 
            if cur >= k:
                res += 1
        return res
