class Solution {
    fun numberOfAlternatingGroups(elements: IntArray, k: Int): Int {
        val size = elements.size
        var groupCount = 1
        var alternatingGroupsCount = 0
        var previousIndex = 0

        for (i in 1 until size + k - 1) {
            val currentIndex = i % size

            if (elements[previousIndex] != elements[currentIndex]) {
                groupCount++
            } else {
                groupCount = 1
            }

            if (groupCount >= k) {
                alternatingGroupsCount++
            }

            previousIndex = currentIndex
        }

        return alternatingGroupsCount
    }
}
