class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        non_alternating_indices = []
        for i, color in enumerate(colors):
            if (color == colors[i - 1]) or (color == colors[(i + 1) % len(colors)]):
                non_alternating_indices.append(i)
        if not non_alternating_indices:
            return len(colors)
        ret = 0
        if non_alternating_indices[0] + (len(colors) - non_alternating_indices[-1]) + 1 >= k:
            ret += non_alternating_indices[0] + len(colors) - non_alternating_indices[-1] + 2 - k
        ret += sum(a - b + 2 - k if a - b + 1 >= k else 0 for a, b in zip(non_alternating_indices[1:], non_alternating_indices[:-1]))
        return ret
