class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(); 
        for (int i = 0; i < n; i++)
            colors.push_back(colors[i]);

        int cur = 0;
        for (int i = 1; i < k; i++)
            cur += colors[i] != colors[i - 1];

        int ans = 0;
        for (int i = k; i <= n + k - 2; i++) {
            if (cur == k - 1) ++ans;
            cur += colors[i] != colors[i - 1];
            cur -= colors[i - k + 1] != colors[i - k];
        }
        return ans + (cur == k - 1);
    }
};
