class Solution {
    #define ll long long int
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        vector<ll>v;
        for(int i=0;i<c.size();i++){
            v.push_back(c[i]);
        }
          for(int i=0;i<k-1;i++){
            v.push_back(c[i]);
        }

        ll ans=0,s=0;
        ll f=0;
         if(v[0]==0)f=0;
         else f=1;
        for(int i=0;i<v.size();i++){
            if(v[i]==0&&f==0){
                s++;
                f=1;
            }else if(v[i]==1&&f==1){
                s++;
                f=0;
            }else{
                if(s>=k){
                ans+=(s-k+1);
                }
                s=0;
                if(v[i]==0)f=0;
                else f=1;
                i--;
            }
        }if(s>=k){
                ans+=(s-k+1);
                }
        return ans;
    }
};
