class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& co, int k) {
        int c=1,j=1,r=0,n=co.size(),p=0;
        bool f=0;
        while(1)
        {
            if(f&&k-1==j)break;
            if(co[j]!=co[p])
            {
                c++;
                if(c==k){r++;c--;}
            }
            else c=1;
            p=j;
            j=(j+1)%n;
            if(p>j)f=1;
            //cout<<j<<p<<f<<r<<endl;
        }
        return r;
    }
};
