class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        for(int i=0;i<k-1;i++) arr.push_back(arr[i]);
        int n=arr.size();
        int cnt=1;
        int ele=arr[0];
        int ans=0;
        for(int i=1;i<n;i++){
            if(cnt==k){
                ans++;
                cnt--;
            }
            if(arr[i]!=arr[i-1]) cnt++;
            else{
                cnt=1;
                ele=arr[i];
            }
        }
        if(cnt==k) ans++;
        return ans;
    }
};
