class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        vector<int> b = a;
        for(int i = 0; i < k - 1; i++) b.push_back(a[i]);
        int n = b.size();
        vector<int> c;
        for(int i = 1; i < n; i++) {
            c.push_back(b[i - 1] != b[i]);
        }
        for(int i = 1; i < c.size(); i++) c[i] += c[i - 1];
    // for(auto i : c) cout << i << " ";
    // cout << "\n";
        int ans = 0;
        k-=2;
        for(int i = k; i < c.size(); i++) {
            int sum = c[i];
            if(i-k-1 >= 0) sum -= c[i-k-1];
            ans += sum == (k+1);
        }
        return ans;

    }
};
