class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int n = colors.size();
        vector<int> pref;

        for(int i=0;i<n;i++)
        {
            int p1 = colors[i];
            int p2 = colors[(i+1)%n];
            if(p1!=p2)
                pref.push_back(1);
            else
                pref.push_back(0);
        }

        for(int i=1;i<n;i++)
            pref[i]+=pref[i-1];
        k--;
        int ans = 0;
        for(int i=0;i<n;i++)
        {
            int r = (i+k-1)%n;
            int l = i;

            if(r<l)
            {
                int p1 = pref[n-1];
                if(l>0)
                    p1-=pref[l-1];
                p1+=pref[r];

                if(p1>=k) ans++;

            }
            else
            {
                int p1 = pref[r];
                if(l>0)
                    p1-=pref[l-1];

                if(p1>=k) ans++;
            }


        }
        return ans;


    }
};
