class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        arr.insert(arr.end(), arr.begin(), arr.begin()+k-1);
        int n = arr.size(), start = 0, end = 1;
        for (auto i = 1; i<arr.size(); ++i){
            end = (arr[i] != arr[i-1]) ? end + 1 : 1;
            if (end >= k){
                ++start;
            }
        }
        return start;
    }
};
