class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> A(n), B(n);
        int curr = 0;
        for(int i=0;i<n;i++, curr^=1){
            if(i-1>=0)A[i] = A[i-1];
            if(colors[i] != curr){
                A[i]++;
            }
        }
        curr = 1;
        for(int i=0;i<n;i++, curr^=1){
            if(i-1>=0)B[i] = B[i-1];
            if(colors[i] != curr){
                B[i]++;
            }
        }
        int ans = 0;

        for(int i = 0;i<n;i++){
            if(colors[i]) {
                if(i&1){
                    if(i+k-1>=n){
                        if(A[n-1] - A[i-1])continue;
                        if((n-i)%2==0){
                            if(B[k-(n-i)-1])continue;
                            ans++;
                        }else{
                            if(A[k-(n-i)-1])continue;
                            ans++;
                        }
                    }else{
                        if(A[i+k-1]-A[i-1])continue;
                        ans++;
                    }
                }else{
                    if(i+k-1>=n){
                        if(B[n-1] - (i-1>=0?B[i-1]:0))continue;
                        if((n-i)%2==0){
                            if(B[k-(n-i)-1])continue;
                            ans++;
                        }else{
                            if(A[k-(n-i)-1])continue;
                            ans++;
                        }
                    }else{
                        if(B[i+k-1]-(i-1>=0?B[i-1]:0))continue;
                        ans++;
                    }
                }
            } else {
                if(i%2==0){
                    if(i+k-1>=n){
                        if(A[n-1] - (i-1>=0?A[i-1]:0))continue;
                        if((n-i)%2==0){
                            if(A[k-(n-i)-1])continue;
                            ans++;
                        }else{
                            if(B[k-(n-i)-1])continue;
                            ans++;
                        }
                    }else{
                        if(A[i+k-1]-(i-1>=0?A[i-1]:0))continue;
                        ans++;
                    }
                }else{
                    if(i+k-1>=n){
                        if(B[n-1] - (i-1>=0?B[i-1]:0))continue;
                        if((n-i)%2==0){
                            if(A[k-(n-i)-1])continue;
                            ans++;
                        }else{
                            if(B[k-(n-i)-1])continue;
                            ans++;
                        }
                    }else{
                        if(B[i+k-1]-(i-1>=0?B[i-1]:0))continue;
                        ans++;
                    }
                }
            }
        }
        return ans;

    }
};
