class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
         int n = colors.size();
        int count = 0;
        int alternatingCount = 0;

        vector<int> ec(colors.begin(), colors.end());
        ec.insert(ec.end(), colors.begin(), colors.begin() + k - 1);


        for (int i = 0; i < k - 1; ++i) {
            if (ec[i] != ec[i + 1]) {
                alternatingCount++;
            }
        }


        for (int i = 0; i < n; ++i) {

            if (alternatingCount == k - 1) {
                count++;
            }


            if (ec[i] != ec[i + 1]) {
                alternatingCount--;
            }

            if (ec[i + k - 1] != ec[i + k]) {
                alternatingCount++;
            }
        }

        return count;
    }
};
