class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if(k == 1) return n;

        int ans = 0;
        int length = 1, i = 1;
        while(i < n+k-1) {
            int current = i % n, prev = (i-1) % n;
            if(colors[current] != colors[prev]) {
                length++;
            } else {
                length = 1;
            }
            if(length >= k) {
                ans++;
            }
            i++;
        }
        return ans;
    }
};
