class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        vector<int> ans(n+k-1);
        for(int i=0;i<n+k-1;i++){
            ans[i]=c[i%n];
        }
        // for(int i=0;i<n+k-1;i++){
        //     cout<<ans[i]<<" ";
        // }
        // cout<<endl;
        // return 0;
        int ans1=0;
        int cnt=0;
        for(int i=0;i<n+k-1;i++){
            if(i==0){
                cnt++;
                continue;
            } 
            if(ans[i]!=ans[i-1]){
                cnt++;
            }
            else{
                cnt=1;
                continue;
            }
            if(cnt==k){
                cnt=cnt-1;
                ans1+=1;
            }
        }
        if(cnt==k) ans1+=1;
        return ans1;
    }
};
