class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        vector<int> nums(n+k-1);

        for(int i=0;i<n+k-1;i++){
            nums[i]=colors[i%n];
        }

        int oddsum=0,evensum=0;
        int ans=0;

        for(int i=0;i<k;i++){
            if(i&1)oddsum+=nums[i];
            else evensum+=nums[i];
        }
        // cout<<evensum<<" "<<oddsum<<endl;
        if(k&1){
            if(evensum==(k+1)/2 && oddsum==0){
                ans++;        
                // cout<<0<<" ";
            } 
            if(evensum==0 && oddsum==k/2)ans++;   
        } 
        else{
            if(evensum==k/2 && oddsum==0 || oddsum==k/2 && evensum==0){
                ans++;
                // cout<<0<<" ";
            }
        }

        for(int i=k;i<n+k-1;i++){
            if(i&1)oddsum+=nums[i];
            else evensum+=nums[i];
            int j=i-k;
            if(j&1)oddsum-=nums[j];
            else evensum-=nums[j];

            if(k&1){
                if(i&1){
                    if(nums[i]==1 && oddsum==(k+1)/2 && evensum==0){
                        ans++;
                        // cout<<j+1<<" ";
                    }
                    if(nums[i]==0 && oddsum==0 && evensum==(k)/2){
                        ans++;
                        // cout<<j+1<<" ";
                    }
                }
                else{
                    if(nums[i]==1 && evensum==(k+1)/2 && oddsum==0){
                        ans++;
                        // cout<<j+1<<" ";
                    }
                    if(nums[i]==0 && evensum==0 && oddsum==(k)/2){
                        ans++;
                        // cout<<j+1<<" ";
                    }
                }

            }
            else{
                if(evensum==k/2 && oddsum==0 || oddsum==k/2 && evensum==0){
                    ans++;
                    // cout<<j+1<<" ";
                }
            }

        }
        return ans;
    }
};
