class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int num = colors.size(); 
        int totalcnt = 0; 

        if (k == 1) { 
            return num; 
        } 

        int len = 1; 
        int chk = 1; 

        while (chk < num + k - 1) { 
            int curnt = chk; 
            int prv = chk - 1; 

            if (curnt >= num) { 
                curnt -= num; 
            } 

            if (prv >= num) { 
                prv -= num; 
            } 

            if (colors[curnt] != colors[prv]) { 
                len++; 
            } 

            else { 
                len = 1; 
            } 

            if (len >= k) { 
                totalcnt++; 
            } 

            chk++; 
        } 

        return totalcnt; 
    }
};
