class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int b) {
       vector<int> dp;
       for(int i=colors.size()-b+1; i< colors.size(); i++)
       {
        dp.push_back(colors[i]);
       }
       for(int i=0;i <colors.size();i++)
       {
        dp.push_back(colors[i]);
       }
       int ans=0;
       int k=1;
       for(int i=1;i<dp.size(); i++)
       {
        if(dp[i]!=dp[i-1])
        {
            k++;
        }
        else
        {
            if(k>=b)
            {
                ans+=k-b+1;
            }
            k=1;
        }
       }
       if(k>=b)
       {
        ans+=k-b+1;
       }
       return ans;
    }
};
