class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n=colors.length;
        int cnt=1;
        int ans=0;
        for(int i=0;i<n+k-2;i++){
            int index=i%n;
            if(colors[index]!=colors[(index+1)%n]){
                cnt++;
            }else{
                cnt=1;
            }
            if(cnt>=k){
            ans++;
            // cnt=1;
            }
        }
        return ans;
    }
}
