class Solution { 
public: 
  int numberOfAlternatingGroups(vector<int>& colors, int k) {  
        int n = colors.size();        
  int alter = 1;  
        if (k == 1) {         
            return n;  
        }  
   int count =0; 

        int i = 1;  
        while (i < n + k - 1) {  
            int c = i;  
            int p = i - 1;  


            if (p >= n) {  
                p -= n;  
            }  

             if (c >= n) {  
                c -= n;  
            }
            if (colors[c] != colors[p]) {  
                ++alter;  
            } else {  
                alter = 1;  
            }  

            if (alter >= k) {  
                ++count;  
            }  

            ++i;  
        }  

        return count;  
    } 
};
