class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int count=0;
        long long len=1;
        for(int i=1;i<k;i++){
            if(colors[i]!=colors[i-1]){
                len++;
            }
            else{
                len=1;
            }
            if(len>=k)count++;
        }
        for(int i=k;i<n+k-1;i++){
            int x=i%n;
            int y=(i-1)%n;
            if(colors[x]!=colors[y]){
                len++;
            }
            else{
                len=1;

            }
            if(len>=k)count++;
        }
        return count;
    }
};
