class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int alter_count = 1, res = 0;
        int n = colors.size();
        for(int i = n - k; i < n; i++) {
            if (colors[i % n] != colors[(i+n-1) % n]) {
                alter_count++;
            }else {
                alter_count = 1;
            }
        }
        for(int i = 0; i < n; i++) {
            if (colors[i % n] != colors[(i+n-1) % n]) {
                alter_count++;
            }else {
                alter_count = 1;
            }
            if (alter_count >= k) {
                res++;
            }
        }
        return res;
    }
};
