class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        N = len(colors)
        count = 0

        if k == 1:
            return N

        alternating_length = 1
        i = 1
        while i < N + k - 1:
            current = i
            previous = i - 1

            if current >= N:
                current -= N
            if previous >= N:
                previous -= N

            if colors[current] != colors[previous]:
                alternating_length += 1
            else:
                alternating_length = 1

            if alternating_length >= k:
                count += 1

            i += 1

        return count
