class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        int cnt = 1;
        int last = colors[0];

        for(int i = 1; i < n + k - 1; ++i){
            int j = i % n;
            int cur = colors[j];
            if(cur != last) cnt ++;
            else cnt = 1;

            if(cnt == k){
                ans ++;
                cnt --;
            } 
            last = cur;
        }

        return ans;
    }
};
