class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0,i = 0,j = 0,prevColor = -1;
        while (j < 2 * colors.size()) {
            while (j < 2 * colors.size() && colors[j % colors.size()] != prevColor) {
                prevColor = colors[j % colors.size()];
                j++;
            }

            while (i < colors.size() && i < j && i + k - 1 < j) {
                ans++;
                i++;
            }

            i = j;
            prevColor = -1;
        }

        return ans;
    }
};
