class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n=v.size();
        for(int i=0; i<n; i++)v.push_back(v[i]);
        int i=0, j=0, res=0;
        // for(int i=0; i<v.size(); i++){
        //     cout << v[i] << " ";
        // }
        // cout << endl;
        while(i<n+k-1){
            int j=i+1;
            while(j<n+k-1 && v[j]!=v[j-1])j++;
            int len=j-i;
            // cout << i << " " << j << " " << len << endl;
            int cur=max(0, len-k+1);
            res+=cur;
            i=j;
        }
        return res;
    }
};
