class Solution {
 public int numberOfAlternatingGroups(int[] colors, int k) {
        int ans = 0;
        int n = colors.length;

        List<Integer> extendedColors = new ArrayList<>();
        for (int color : colors) {
            extendedColors.add(color);
        }
        for (int i = 0; i < k - 1; i++) {
            extendedColors.add(colors[i]);
        }

        int count = 0;
        int prev = (extendedColors.get(0) == 0 ? 1 : 0);

        // Iterate through the extended array
        for (int i = 0; i < extendedColors.size(); i++) {
            if (prev == extendedColors.get(i)) {
                count = 1;
            } else {
                count++;
            }
            prev = extendedColors.get(i);
            if (count >= k) {
                ans++;
            }
        }

        return ans;
    }
}
