class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        vector<int> v;
        for(int i = 0;i<n;i++){
            v.push_back(colors[i]);
        }
        for(int i = 0;i<k-1;i++){
            v.push_back(colors[i]);
        }
        int sz = 1;
        for(int i = 1;i<v.size();i++){
            if(v[i] != v[i-1]){
                sz++;
            }
            else{
                ans += max(0,sz - k + 1);
                sz = 1;
            }
        }
        ans += max(0,sz - k + 1);
        return ans;
    }
};
