class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int res = 0;
        int cnt = 1;
        int n = colors.size();
        int m = n+k-1;

        for (int i=1; i<m; ++i) {
            if (colors[i%n] != colors[(i-1+n)%n]) {
                cnt++;
                if (cnt >= k)
                    res++;
            } else cnt = 1;
        }

        return res;
    }
};
