class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        for(int i=0;i<k-1;i++)v.push_back(v[i]);

        int i=0,j=0;
        int prev=-1;
        int ans=0,cur=0;
        while(j<v.size()){

            if(prev==-1){
                prev=v[j];
                j++;

            }

            else if(prev!=v[j]){

                prev=v[j];
                j++;
            }
            else{
                if(j-i>=k)ans+=(j-i)-(k-1);

                i=j;
                prev=v[j];
                j++;
            }
        }

        if(j-i>=k)ans+=(j-i)-(k-1);
        return ans;
    }
};
