class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(),ans=0;
        for(int i=0;i<n;i++){
            colors.push_back(colors[i]);
        }
        n=colors.size();
        vector<int> cnt(n,0);
        for(int i=1;i<n;i++){
            if(colors[i]!=colors[i-1]){
                cnt[i]=1;
            }
        }
        for(int i=1;i<n;i++){
            cnt[i]+=cnt[i-1];
        }
        // cout<<n<<endl;
        // for(auto i:cnt){
        //     cout<<i<<" ";
        // }
        // cout<<endl;
        for(int i=0;i<n/2;i++){
            int dum=cnt[i+k-1]-cnt[i];
            // cout<<dum<<" ";
            // if(i-1>=0){
            //     dum-=cnt[i-1];
            // }
            if(dum>=k-1){
                ans++;
            }
        }
        // cout<<endl;
        return ans;
    }
};
