class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        int n=colors.size();
        vector<int> val(n);
        for(int i=0;i<n-1;i++){
            if(colors[i]!=colors[i+1]){
                val[i]=1;
            }
            else{
                val[i]=0;
            }
        }
        vector<int> pref(n);
        pref[0]=val[0];
        cout<<pref[0]<<" ";
        for(int i=1;i<n-1;i++){
            pref[i]=pref[i-1]+val[i];
            cout<<pref[i]<<" ";
        }
        cout<<endl;
        int ans=0;
        for(int i=0;i<n-1;i++){
            int a=0;
            if((i-(k-1))>=0){
                a=pref[i-(k-1)];
            }
            if((pref[i]-a)==k-1){
                ans++;
            }
        }

        return ans;


    }
};
