class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& val, int k) {
         int pkk = 0,i=0,j=1;
        int p = val.size();

        while (j < p + k && i < p) {
            int sk = j;
            int rr = sk % p;

            while (val[rr] != val[(rr + p - 1) % p] && (sk < (p + k - 1))) {
                sk++,rr = sk % p;
            }
            pkk += max(sk - i - k + 1, 0);
            i = sk,j = i + 1;
        }

        return pkk;

    }
};
