class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& cols,int klk) {


        int cols_len=cols.size();
        int cnt=0;
        if (klk==1) {
             return cols_len;
        }

        int alter_lgn3=1;
        int i=1;

        while (i<cols_len+klk-1) {

            int ptr1=i;
            int ptr01=i-1;
            if (ptr1>=cols_len) {
                ptr1-=cols_len;
            }
            if (ptr01>=cols_len) {
                ptr01-=cols_len;
            }

            if (cols[ptr1]!=cols[ptr01]) {
                ++alter_lgn3;
            } else {
                alter_lgn3=1;
            }
            if (alter_lgn3>=klk) {
                ++cnt;
            }

            ++i;


        }

        return cnt;
    }
};
