class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int n = colors.size();
        vector<int>c;
        for(int i=n-k+1;i<n;i++)
        c.push_back(colors[i]);

        for(int i=0;i<n;i++)
        c.push_back(colors[i]);
        n = c.size();

        int pre1Z[n+1];
        int pre1O[n+1];
        int pre2Z[n+1];
        int pre2O[n+1];
        pre1Z[0]=0;
        pre1O[0]=0;
        pre2Z[0]=0;
        pre2O[0]=0;


        for(int i=0;i<n;i++){
            if(i%2==0){
                if(c[i]==0){
                    pre1Z[i+1] =pre1Z[i]+1; 
                    pre1O[i+1]=pre1O[i];
                    pre2Z[i+1]=pre2Z[i];
                    pre2O[i+1]= pre2O[i];


                }
                else
                {
                    pre1Z[i+1] =pre1Z[i]; 
                    pre1O[i+1]=pre1O[i]+1;
                    pre2Z[i+1]=pre2Z[i];
                    pre2O[i+1]= pre2O[i];
                }
            }
            else
            {
                if(c[i]==0){
                    pre1Z[i+1] =pre1Z[i]; 
                    pre1O[i+1]=pre1O[i];
                    pre2Z[i+1]=pre2Z[i]+1;
                    pre2O[i+1]= pre2O[i];
                }
                else
                {
                    pre1Z[i+1] =pre1Z[i]; 
                    pre1O[i+1]=pre1O[i];
                    pre2Z[i+1]=pre2Z[i];
                    pre2O[i+1]= pre2O[i]+1;
                }
            }
        }
        int ans=0;
        for(int i=k;i<=n;i++){
            int p1Z = pre1Z[i]-pre1Z[i-k];
            int p1O = pre1O[i]-pre1O[i-k];
            int p2Z = pre2Z[i]-pre2Z[i-k];
            int p2O = pre2O[i]-pre2O[i-k];
            // cout<<p1Z<<" "<<p1O<<" "<<p2Z<<" "<<p2O<<"\n";
            if(k%2==0){
                if(p1Z==k/2 && p2O==k/2){
                    ans++;
                }
                else if(p1O==k/2 && p2Z==k/2){
                    ans++;
                }
            }
            else
            {
                if(p1Z==k/2 && p2O==(k+1)/2){
                    ans++;
                }
                else if(p1O==k/2 && p2Z==(k+1)/2){
                    ans++;
                }
                else if(p1Z==(k+1)/2 && p2O==k/2){
                    ans++;
                }
                else if(p1O==(k+1)/2 && p2Z==k/2){
                    ans++;
                }
            }
        }
        // cout<<"\n";

        return  ans;
    }
};
