class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(),ans=0,j=1;

        if(k==1) return n;

        vector<int> v;

        v=colors;

        int i=0;

        while(i<k-1)
        {
            v.push_back(colors[i]);
            i++;
        }

        i=0;

        n=v.size();

        for(int j=1;j<n;j++)
        {
            if(v[j]!=v[j-1])
            {
                if(j-i+1==k)
                {
                    ans++;
                    i++;
                }
            }
            else{
                i=j;
            }
        }

        return ans;
    }
};
